import React, { useState } from 'react';
import './App.css';
import PaymentForm from './components/PaymentForm';
import PaymentSuccess from './components/PaymentSuccess';

function App() {
  const [paymentStatus, setPaymentStatus] = useState('pending');
  const [paymentDetails, setPaymentDetails] = useState(null);

  const handlePaymentSuccess = (details) => {
    setPaymentDetails(details);
    setPaymentStatus('success');
  };

  const handlePaymentFailure = (error) => {
    setPaymentDetails({ error });
    setPaymentStatus('failed');
  };

  const resetPayment = () => {
    setPaymentStatus('pending');
    setPaymentDetails(null);
  };

  return (
    <div className="App">
      {/* Animated SVG trees background */}
      <div style={{position: 'fixed', bottom: 0, left: 0, width: '100%', zIndex: 0, pointerEvents: 'none'}}>
        <svg width="100%" height="180" viewBox="0 0 1440 180" fill="none" xmlns="http://www.w3.org/2000/svg" style={{display: 'block'}}>
          {/* Ground */}
          <rect y="140" width="1440" height="40" fill="#7ec850" />
          {/* Tree 1 */}
          <g className="tree-anim" style={{animationDelay: '0s'}}>
            <rect x="100" y="100" width="16" height="40" rx="8" fill="#8d6748" />
            <ellipse cx="108" cy="100" rx="32" ry="28" fill="#3cb371" />
          </g>
          {/* Tree 2 */}
          <g className="tree-anim" style={{animationDelay: '1s'}}>
            <rect x="300" y="110" width="12" height="30" rx="6" fill="#8d6748" />
            <ellipse cx="306" cy="110" rx="22" ry="18" fill="#4caf50" />
          </g>
          {/* Tree 3 */}
          <g className="tree-anim" style={{animationDelay: '0.5s'}}>
            <rect x="600" y="120" width="14" height="35" rx="7" fill="#8d6748" />
            <ellipse cx="607" cy="120" rx="26" ry="20" fill="#388e3c" />
          </g>
          {/* Tree 4 */}
          <g className="tree-anim" style={{animationDelay: '1.5s'}}>
            <rect x="900" y="105" width="18" height="45" rx="9" fill="#8d6748" />
            <ellipse cx="909" cy="105" rx="34" ry="26" fill="#43cea2" />
          </g>
          {/* Tree 5 */}
          <g className="tree-anim" style={{animationDelay: '0.8s'}}>
            <rect x="1200" y="115" width="13" height="33" rx="6.5" fill="#8d6748" />
            <ellipse cx="1206" cy="115" rx="20" ry="16" fill="#56ab2f" />
          </g>
        </svg>
      </div>

      <header className="App-header">
        <h1>🌳 Vanseva</h1>
        <p>Grow a greener tomorrow — support tree plantation with UPI</p>
      </header>

      <main className="App-main">
        {paymentStatus === 'pending' && (
          <PaymentForm 
            onSuccess={handlePaymentSuccess}
            onFailure={handlePaymentFailure}
          />
        )}

        {paymentStatus === 'success' && (
          <PaymentSuccess 
            details={paymentDetails}
            onReset={resetPayment}
          />
        )}

        {paymentStatus === 'failed' && (
          <div className="payment-failed">
            <h2>❌ Payment Failed</h2>
            <p>{paymentDetails?.error || 'Something went wrong'}</p>
            <button onClick={resetPayment} className="retry-button">
              🔄 Try Again
            </button>
          </div>
        )}
      </main>

      <footer className="App-footer">
        <p>🌱 Every payment helps plant a tree</p>
      </footer>
      <style>{`
        .tree-anim {
          transform-origin: bottom center;
          animation: sway 3s ease-in-out infinite alternate;
        }
        @keyframes sway {
          0% { transform: rotate(-2deg) scaleY(1); }
          50% { transform: rotate(2deg) scaleY(1.04); }
          100% { transform: rotate(-2deg) scaleY(1); }
        }
      `}</style>
    </div>
  );
}

export default App;
