// PhonePe Payment App Configuration
const config = {
  // Replace this with your actual UPI ID
  UPI_ID: 'technoarch@paytm',

  // App settings
  APP_NAME: 'PhonePe Payment App',
  APP_DESCRIPTION: 'Secure and Fast Payments',

  // Payment settings
  CURRENCY: 'INR',
  MIN_AMOUNT: 1,
  MAX_AMOUNT: 100000,

  // UI settings
  PRIMARY_COLOR: '#5f3dc4',
  SECONDARY_COLOR: '#7048e8',
  SUCCESS_COLOR: '#28a745',
  ERROR_COLOR: '#e74c3c',

  // Contact information
  SUPPORT_EMAIL: 'support@example.com',
  SUPPORT_PHONE: '+91-XXXXXXXXXX'
};

export default config;
