import React, { useState } from 'react';
import './App.css';
import PaymentForm from './components/PaymentForm';
import PaymentSuccess from './components/PaymentSuccess';

function App() {
  const [paymentStatus, setPaymentStatus] = useState('pending');
  const [paymentDetails, setPaymentDetails] = useState(null);

  const handlePaymentSuccess = (details) => {
    setPaymentDetails(details);
    setPaymentStatus('success');
  };

  const handlePaymentFailure = (error) => {
    setPaymentDetails({ error });
    setPaymentStatus('failed');
  };

  const resetPayment = () => {
    setPaymentStatus('pending');
    setPaymentDetails(null);
  };

  return (
    <div className="App">
      {/* Floating particles for visual appeal */}
      <div className="floating-particles">
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
      </div>

      <header className="App-header">
        <h1>üì± PhonePe Payment App</h1>
        <p>‚ú® Secure and Fast Payments with Modern Design</p>
      </header>

      <main className="App-main">
        {paymentStatus === 'pending' && (
          <PaymentForm 
            onSuccess={handlePaymentSuccess}
            onFailure={handlePaymentFailure}
          />
        )}

        {paymentStatus === 'success' && (
          <PaymentSuccess 
            details={paymentDetails}
            onReset={resetPayment}
          />
        )}

        {paymentStatus === 'failed' && (
          <div className="payment-failed">
            <h2>‚ùå Payment Failed</h2>
            <p>{paymentDetails?.error || 'Something went wrong'}</p>
            <button onClick={resetPayment} className="retry-button">
              üîÑ Try Again
            </button>
          </div>
        )}
      </main>

      <footer className="App-footer">
        <p>üöÄ Built with React & Modern Web Technologies</p>
        <p>üîí Secure ‚Ä¢ Fast ‚Ä¢ Reliable</p>
      </footer>
    </div>
  );
}

export default App;
