import React, { useState } from 'react';
import './PaymentForm.css';
import config from '../config';

const PaymentForm = ({ onSuccess, onFailure }) => {
  const [formData, setFormData] = useState({
    amount: '',
    name: '',
    email: '',
    phone: ''
  });
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    if (!formData.amount || formData.amount < config.MIN_AMOUNT || formData.amount > config.MAX_AMOUNT) {
      return `Please enter a valid amount between â‚¹${config.MIN_AMOUNT} and â‚¹${config.MAX_AMOUNT}`;
    }
    if (!formData.name.trim()) {
      return 'Please enter your name';
    }
    if (!formData.email.trim() || !/\S+@\S+\.\S+/.test(formData.email)) {
      return 'Please enter a valid email';
    }
    if (!formData.phone.trim() || formData.phone.length < 10) {
      return 'Please enter a valid phone number';
    }
    return null;
  };

  const initiatePhonePePayment = () => {
    // PhonePe UPI payment URL format
    const upiId = config.UPI_ID;
    const amount = formData.amount;
    const name = encodeURIComponent(formData.name);
    const note = encodeURIComponent('Payment for services');

    // Create PhonePe UPI payment URL
    const phonePeUrl = `upi://pay?pa=${upiId}&pn=${name}&am=${amount}&tn=${note}&cu=${config.CURRENCY}`;

    // For web, we'll use a fallback approach
    // In a real app, you would integrate with PhonePe's API
    try {
      // Try to open PhonePe app
      window.location.href = phonePeUrl;

      // Fallback: Show instructions
      setTimeout(() => {
        const fallbackMessage = `
          If PhonePe app didn't open automatically, please:
          1. Open PhonePe app manually
          2. Scan this UPI ID: ${upiId}
          3. Enter amount: â‚¹${amount}
          4. Add note: ${note}
        `;

        if (confirm(fallbackMessage)) {
          // Simulate successful payment for demo
          setTimeout(() => {
            onSuccess({
              transactionId: 'TXN' + Date.now(),
              amount: amount,
              timestamp: new Date().toISOString(),
              upiId: upiId
            });
          }, 2000);
        }
      }, 3000);

    } catch (error) {
      onFailure('Failed to initiate payment. Please try again.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const validationError = validateForm();
    if (validationError) {
      onFailure(validationError);
      return;
    }

    setIsLoading(true);

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Initiate PhonePe payment
      initiatePhonePePayment();

    } catch (error) {
      onFailure('Payment initiation failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="payment-form-container">
      <div className="payment-form-card">
        <div className="form-header">
          <h2>Make Payment</h2>
          <p>Enter your details to proceed with PhonePe payment</p>
        </div>

        <form onSubmit={handleSubmit} className="payment-form">
          <div className="form-group">
            <label htmlFor="amount">Amount (â‚¹)</label>
            <input
              type="number"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleInputChange}
              placeholder={`Enter amount (â‚¹${config.MIN_AMOUNT} - â‚¹${config.MAX_AMOUNT})`}
              min={config.MIN_AMOUNT}
              max={config.MAX_AMOUNT}
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Enter your full name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="phone">Phone Number</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              placeholder="Enter your phone number"
              required
            />
          </div>

          <button 
            type="submit" 
            className="phonepe-pay-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <span className="loading-spinner">Processing...</span>
            ) : (
              <>
                <span className="phonepe-icon">ðŸ“±</span>
                Pay with PhonePe
              </>
            )}
          </button>
        </form>

        <div className="payment-info">
          <p>ðŸ”’ Secure payment powered by PhonePe</p>
          <p>ðŸ’³ UPI ID: {config.UPI_ID}</p>
        </div>
      </div>
    </div>
  );
};

export default PaymentForm;
